import React from 'react'

import mongodb from 'mongodb';

import{ useState, useEffect } from 'react';

require("../db/conn");

// const MongoClient = mongodb.MongoClient;
const enquiry = mongodb.enquiry;
// const uri = 'mongodb://localhost:27017/myDatabase'; // Replace with your MongoDB URI
const client = new enquiry("mongodb+srv://swathipardeshi:swathi01!@cluster0.y5stpf9.mongodb.net/merstack/contacts", { useNewUrlParser: true, useUnifiedTopology: true });

function Enquiry() {
  const [data, setData] = useState([]);

  useEffect(() => {
    client.connect((err) => {
      if (err) {
        console.log(err);
        return;
      }
      const collection = client.db("mernstack").collection("contacts"); // Replace with your collection name
      collection.find().toArray((err, data) => {
        if (err) {
          console.log(err);
          return;
        }
        setData(data);
      });
    });
  }, []);

  return (
    <div>
      {data.map((item) => (
        <div key={item._id}>
          <h3>{item.title}</h3>
          <p>{item.description}</p>
        </div>
      ))}
    </div>
  );
}

export default Enquiry;
